settings.liquid:
{% if current_variant.inventory_quantity <= 0 %}
<div class="pulsered"></div>
                      Ausverkauft
{%- else -%}
<div class="pulse0"></div>
                   Kostenloser Versand | 1-3 Werktagen


{%- endif -%}
<style>
.pulse0 {
    display: inline-block;
    margin-right: 6px;
    margin-left:5px;
    margin-top: 3px;
    width: 12px;
    height: 12px;
    border-radius: 50%;
    background: #27AE60;
    cursor: pointer;
    box-shadow: 0 0 0 rgba(39,174,96, 0.4);
    animation: pulse 2s infinite;
  }
  .pulse0:hover {
    animation: none;
  }

  .pulsered {
    display: inline-block;
    margin-right: 6px;
    margin-left:5px;
    margin-top: 3px;
    width: 12px;
    height: 12px;
    border-radius: 50%;
    background: rgb(255,0,0);
    cursor: pointer;
    box-shadow: 0 0 0 rgba(255,0,0, 0.4);
    animation: pulsereed 2s infinite;
  }
  .pulsered:hover {
    animation: none;
  }


@-webkit-keyframes pulse {
    0% {
      -webkit-box-shadow: 0 0 0 0 rgba(39,174,96, 0.4);
    }
    70% {
        -webkit-box-shadow: 0 0 0 10px rgba(39,174,96, 0);
    }
    100% {
        -webkit-box-shadow: 0 0 0 0 rgba(39,174,96, 0);
    }
  }
  @keyframes pulse {
    0% {
      -moz-box-shadow: 0 0 0 0 rgba(39,174,96, 0.4);
      box-shadow: 0 0 0 0 rgba(39,174,96, 0.4);
    }
    70% {
        -moz-box-shadow: 0 0 0 10px rgba(39,174,96, 0);
        box-shadow: 0 0 0 10px rgba(39,174,96, 0);
    }
    100% {
        -moz-box-shadow: 0 0 0 0 rgba(39,174,96, 0);
        box-shadow: 0 0 0 0 rgba(39,174,96, 0);
    }
  }

  @-webkit-keyframes pulsereed {
    0% {
      -webkit-box-shadow: 0 0 0 0 rgba(255,0,0, 0.4);
    }
    70% {
        -webkit-box-shadow: 0 0 0 10px rgba(255,0,0, 0);
    }
    100% {
        -webkit-box-shadow: 0 0 0 0 rgba(255,0,0, 0);
    }
  }
  @keyframes pulsereed {
    0% {
      -moz-box-shadow: 0 0 0 0 rgba(255,0,0, 0.4);
      box-shadow: 0 0 0 0 rgba(255,0,0, 0.4);
    }
    70% {
        -moz-box-shadow: 0 0 0 10px rgba(255,0,0, 0);
        box-shadow: 0 0 0 10px rgba(255,0,0, 0);
    }
    100% {
        -moz-box-shadow: 0 0 0 0 rgba(255,0,0, 0);
        box-shadow: 0 0 0 0 rgba(255,0,0, 0);
    }
  }
</style>
